import React from "react";
import NavBar from "./NavBar";
import {useParams, Link} from "react-router-dom";

function Deck({decks, cards}) {
  const {deckId} = useParams();

  // identifies selected deck to view
  const deck = decks.find((item) => item.id === +deckId);

  // identifies cards for selected deck
  const deckCards = cards.filter(item => item.deckId === +deckId);

  // creates a main card listing for deck selected
  // const deckContent =
  //   <div className="col-sm-12">
  //     <div className="card" style={{border: "none"}}>
  //       <div className="card-body">
  //         <h5 className="card-title">{deck.name}</h5>
  //         <p className="card-text">{deck.description}</p>
  //         <div className="d-flex justify-content-between">
  //           <div>
  //             <Link className="btn btn-secondary mr-1">Edit</Link>
  //             <Link className="btn btn-primary mr-1">Study</Link>
  //             <Link className="btn btn-primary"><strong>+ Add Cards</strong></Link>
  //           </div>
  //           <div>
  //             <Link className="btn btn-danger">Delete</Link>
  //           </div>
  //         </div>
  //       </div>
  //     </div>
  //   </div>

    // creates a card listing for each flashcard
    const cardsContent = deckCards.map((item) => {
      return (
        <div className="col-sm-6" key={item.id}>
          <div className="card">
            <div className="card-body">
              <p className="card-text"><strong>Front: </strong>{item.front}</p>
              <p className="card-text"><strong>Back: </strong>{item.back}</p>
              <div className= "d-flex justify-content-end">
                <Link className="btn btn-secondary mr-1">Edit</Link>
                <Link className="btn btn-danger">Delete</Link>
              </div>  
            </div>        
          </div>
        </div>
      )
    })


  return (
    <>
      <NavBar />
      <ViewDeck />

      <div className="row">
        {deckContent}
        {cardsContent}
      </div>


    </>
  )
}

export default Deck;
