import React, {useState} from "react";
import {Link} from "react-router-dom";

function DisplayCard({deckCards}) {

  const [Card, setCard] = useState(deckCards[0]);
  const [side, setSide] = useState(true);

  // determines current count of selected card
  const currentCard = deckCards.indexOf(Card) + 1;

  function handleFlip() {
    if (side === Card.front) return setSide(Card.back);
    if (side === Card.back) return setSide(Card.front);
  }

  function handleNext() {
    setCard(prevCard => deckCards[deckCards.indexOf(prevCard) + 1]);
    setSide(Card.front);
  }

  // function handlePrev() {

  // }

  return (
    <div className="row">
      <div className="col-sm-6">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Card {currentCard} of {deckCards.length}</h5>
            <p className="card-text">{side}</p>
            <Link className="btn btn-secondary" onClick={handleFlip}>Flip</Link>
            <Link className="btn btn-primary" onClick={handleNext}>Next</Link>
          </div>
        </div>
      </div>
    </div>
  )
}

export default DisplayCard;
